    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
        String current;
        current=jComboBox3.getSelectedItem().toString();
     
        switch(current){
            
        case "Temparature": 
            
        double n;
        String to;
        String from;
        Double result;
        n = Double.parseDouble(jTextField1.getText()) ;
        to  = jComboBox2.getSelectedItem().toString();
        from  = jComboBox1.getSelectedItem().toString();
         
          if ( from.equals("Celsius") ){
           switch(to){
               case "Fahrenheit" :
                    result = ((9.0 / 5.0 ) * n) + 32;
                    jTextField2.setText(result.toString());
                   break ;
               case "Kelvin" :
                    result = (n*1)+273.15;
                    jTextField2.setText(result.toString());
                    break ;
               default  :
                    result = n *1;
                    jTextField2.setText(result.toString());                 
          }
       }
           if ( from.equals("Fahrenheit") ){
           switch(to){
               case "Celsius" :
                    result = (n-32)*5/9;
                    jTextField2.setText(result.toString());
                   break ;
               case "Kelvin" :
                    result = ((n-32)*5/9)+273.15;
                    jTextField2.setText(result.toString());
                    break ;
               default  :
                    result = n *1;
                    jTextField2.setText(result.toString());                 
          }
       }
           if ( from.equals("Kelvin") ){
           switch(to){
               case "Fahrenheit" :
                    result = ((n-273.15)*9/5)+32;
                    jTextField2.setText(result.toString());
                   break ;
               case "Celsius" :
                    result = n-273.15;
                    jTextField2.setText(result.toString());
                    break ;
               default  :
                    result = n *1;
                    jTextField2.setText(result.toString());                 
          }
           }   
           
       case "Lenght":
              
        double nn;
        String too;
        String fromm;
        Double resultt;
        nn = Double.parseDouble(jTextField1.getText()) ;
        too  = jComboBox2.getSelectedItem().toString();
        fromm  = jComboBox1.getSelectedItem().toString();
         
          if ( fromm.equals("Kilometer") ){
           switch(too){
               case "Meter" :
                    resultt =nn*1000;
                    jTextField2.setText(resultt.toString());
                   break;
               
                    case "Inches" :
                    resultt = nn*39370.08;
                    jTextField2.setText(resultt.toString());
                    break ;
                    case "Centimeter" :
                    resultt = nn*100000;
                    jTextField2.setText(resultt.toString());
                    break; 
               default  :
                    resultt = nn *1;
                    jTextField2.setText(resultt.toString());                 
          }
       }
           if ( fromm.equals("Meter") ){
           switch(too){
               case "Kilometer" :
                    resultt = nn/1000;
                    jTextField2.setText(resultt.toString());
                   break ;
               case "Inches" :
                    resultt = nn*39.3701;
                    jTextField2.setText(resultt.toString());
                    break ;
                    case "Centimeter" :
                    resultt = nn*100;
                    jTextField2.setText(resultt.toString());
                    break ;
               default  :
                    resultt = nn *1;
                    jTextField2.setText(resultt.toString());                 
          }
       }
              if ( fromm.equals("Inches") ){
           switch(too){
               case "Kilometer" :
                    resultt = nn*0.0000254;
                    jTextField2.setText(resultt.toString());
                   break ;
               case "Meter" :
                    resultt = nn*0.0254;
                    jTextField2.setText(resultt.toString());
                    break ;
                    case "Centimeter" :
                    resultt = nn*2.54;
                    jTextField2.setText(resultt.toString());
                    break ;
               default  :
                    resultt = nn *1;
                    jTextField2.setText(resultt.toString());        
          }
           }   
               if ( fromm.equals("Centimeter") ){
           switch(too){
               case "Kilometer" :
                    resultt = nn*0.00001;
                    jTextField2.setText(resultt.toString());
                   break ;
               case "Meter" :
                    resultt = nn*0.01;
                    jTextField2.setText(resultt.toString());
                    break ;
                    case "Inches" :
                    resultt = nn*0.393701;
                    jTextField2.setText(resultt.toString());
                    break ;
               default  :
                    resultt = nn *1;
                    jTextField2.setText(resultt.toString());
               
    }//GEN-LAST:event_jButton1ActionPerformed
    
           }
      case "Currency":
                   
        double nnn;
        String tooo;
        String frommm;
        Double resulttt;
        nnn = Double.parseDouble(jTextField1.getText()) ;
        tooo  = jComboBox2.getSelectedItem().toString();
        frommm  = jComboBox1.getSelectedItem().toString();
                   
              if ( frommm.equals("US Doller") ){
           switch(tooo){
               case "Euro" :
                    resulttt =nnn*0.89;
                    jTextField2.setText(resulttt.toString());
                   break;
               
                    case "Saudia Riyal" :
                    resulttt = nnn*3.75;
                    jTextField2.setText(resulttt.toString());
                    break ;
                    case "Taka" :
                    resulttt = nnn*78.32;
                    jTextField2.setText(resulttt.toString());
                    break; 
               default  :
                    resulttt = nnn*1;
                    jTextField2.setText(resulttt.toString());                 
          }
       }     
            
               if ( frommm.equals("Euro") ){
           switch(tooo){
               case "US Doller" :
                    resulttt =nnn*1.12;
                    jTextField2.setText(resulttt.toString());
                   break;
               
                    case "Saudia Riyal" :
                    resulttt = nnn*4.21;
                    jTextField2.setText(resulttt.toString());
                    break ;
                    case "Taka" :
                    resulttt = nnn*87.91;
                    jTextField2.setText(resulttt.toString());
                    break; 
               default  :
                    resulttt = nnn*1;
                    jTextField2.setText(resulttt.toString());
              
        }
        
               }
                if ( frommm.equals("Saudia Riyal") ){
           switch(tooo){
               case "Euro" :
                    resulttt =nnn*0.24;
                    jTextField2.setText(resulttt.toString());
                   break;
               
                    case "US Doller" :
                    resulttt = nnn*0.27;
                    jTextField2.setText(resulttt.toString());
                    break ;
                    case "Taka" :
                    resulttt = nnn*20.88;
                    jTextField2.setText(resulttt.toString());
                    break; 
               default  :
                    resulttt = nnn*1;
                    jTextField2.setText(resulttt.toString());   
    }
    }
                
                 if ( frommm.equals("Taka") ){
           switch(tooo){
               case "Euro" :
                    resulttt =nnn*0.011;
                    jTextField2.setText(resulttt.toString());
                   break;
               
                    case "Saudia Riyal" :
                    resulttt = nnn*0.048;
                    jTextField2.setText(resulttt.toString());
                    break ;
                    case "US Doller" :
                    resulttt = nnn*0.013;
                    jTextField2.setText(resulttt.toString());
                    break; 
               default  :
                    resulttt = nnn*1;
                    jTextField2.setText(resulttt.toString());
           }
                 }
        }
    }           
                
