    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        
          String currentt;
        currentt=jComboBox3.getSelectedItem().toString();
        
        switch(currentt){
            case "Temparature":
                jComboBox1.addItem("Celsius");
                jComboBox1.addItem("Fahrenheit");
                jComboBox1.addItem("Kelvin");
                jComboBox2.addItem("Celsius");
                jComboBox2.addItem("Fahrenheit");
                jComboBox2.addItem("Kelvin");
                break;
            case "Lenght":
                jComboBox1.addItem("Kilometer");
                jComboBox1.addItem("Meter");
                jComboBox1.addItem("Inches");
                jComboBox1.addItem("Centimeter");
                jComboBox2.addItem("Kilometer");
                jComboBox2.addItem("Meter");
                jComboBox2.addItem("Inches");
                jComboBox2.addItem("Centimeter");
                break;
              case "Currency":
                jComboBox1.addItem("US Doller");
                jComboBox1.addItem("Euro");
                jComboBox1.addItem("Saudia Riyal");
                jComboBox1.addItem("Taka");
                jComboBox2.addItem("US Doller");
                jComboBox2.addItem("Euro");
                jComboBox2.addItem("Saudia Riyal");
                jComboBox2.addItem("Taka");
                break;
        }    
        
    }//GEN-LAST:event_jButton3ActionPerformed

